    b4 = np.array(self.pick_vector(self.raw_data_happy,1,nc))
    pos_result =pos_result + (a + b1*g + b2*w + b3*n + b4*h)*f;
    
    # PA == 2 order cos(omega*time)
    f = cos(omega*time)
    a = np.array(self.pick_vector(self.raw_data_walker,2,nc))
