    pos_result =pos_result + (a + b1*g + b2*w + b3*n + b4*h)*f;

    # PA == 3 order sin(2*omega*time)
    f = sin(2*omega*time)
    a = np.array(self.pick_vector(self.raw_data_walker,3,nc))
    b1 = np.array(self.pick_vector(self.raw_data_gender,3,nc))
